// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title Escrow
 * @dev A simple escrow contract to hold funds for a transaction between a client and a freelancer.
 * An arbiter is included to resolve potential disputes.
 */
contract Escrow {
    // --- STATE VARIABLES ---
    address public client;
    address public freelancer;
    address public arbiter; // The person who can resolve disputes
    uint256 public amount;  // The amount of funds held in escrow
    bool public isLocked;    // A flag to prevent funds from being released prematurely

    // --- EVENTS ---
    event Funded(uint256 amount);
    event Released(address indexed recipient, uint256 amount);
    event DisputeResolved(address indexed recipient, uint256 amount);

    /**
     * @dev Sets up the escrow agreement with the participating parties.
     * @param _freelancer The address of the party providing the service.
     * @param _arbiter The address of the party who can resolve disputes.
     */
    constructor(address _freelancer, address _arbiter) payable {
        client = msg.sender; // The person who deploys the contract is the client
        freelancer = _freelancer;
        arbiter = _arbiter;
        amount = msg.value; // The amount sent with the contract creation is the escrowed amount
        isLocked = true;

        emit Funded(msg.value);
    }

    /**
     * @dev Throws an error if called by any account other than the client.
     */
    modifier onlyClient() {
        require(msg.sender == client, "Only the client can call this function");
        _;
    }

    /**
     * @dev Throws an error if called by any account other than the arbiter.
     */
    modifier onlyArbiter() {
        require(msg.sender == arbiter, "Only the arbiter can call this function");
        _;
    }

    /**
     * @dev Releases the funds to the freelancer. Can only be called by the client.
     */
    function release() public onlyClient {
        require(isLocked, "Funds are already released or in dispute");
        payable(freelancer).transfer(amount);
        isLocked = false;
        emit Released(freelancer, amount);
    }

    /**
     * @dev Allows the arbiter to resolve a dispute and send the funds to the freelancer.
     */
    function resolveAndRelease() public onlyArbiter {
        require(isLocked, "Funds are already released");
        payable(freelancer).transfer(amount);
        isLocked = false;
        emit DisputeResolved(freelancer, amount);
    }
}

